  step(200)

  val IDX_VALID = 13
  val IDX_SOP = 12
  val IDX_EOP = 8
  val IDX_DATA = 16

  poke(c.rx_st.empty, 0) // not present in 64-bit version
  poke(c.rx_st.err, 0)

  for (line <- signaltap_string.split("\n") if line.nonEmpty) {
    val arr = line.split(" ").filterNot(_.isEmpty)
    val data = BigInt(arr(IDX_DATA).dropRight(1), 16)
    val eop = BigInt(arr(IDX_EOP))
    val sop = BigInt(arr(IDX_SOP))
    val valid = BigInt(arr(IDX_VALID))
    poke(c.rx_st.data, data)
    poke(c.rx_st.eop, eop)
    poke(c.rx_st.sop, sop)
    poke(c.rx_st.valid, valid)
    step(1)
  }

  val resp_data = bfm_avalon_st_data_out.get_data()
  val resp_els = resp_data
    .grouped(2)
    .map((xs: List[Byte]) => xs(1).toInt << 8 | (xs(0).toInt & 0xff))

  resp_els.zipWithIndex.foreach {
    case (resp, exp) =>
      expect(resp == exp, f"sample at addr ${exp}%x, received ${resp}%x")
  }

}
